import React, { useState, useEffect } from "react";
import "./EditDetails.css";

const EditDetails = () => {
  const [userData, setUserData] = useState({
    username: "",
    email: "",
    mobile: "",
  });

  const [isEditing, setIsEditing] = useState({
    username: false,
    email: false,
    mobile: false,
  });

  // Fetch user data from localStorage on component mount
  useEffect(() => {
    const storedUser = JSON.parse(localStorage.getItem("user")) || {
      username: "John Doe",
      email: "johndoe@example.com",
      mobile: "+919876543210",
    };
    setUserData(storedUser);
  }, []);

  const handleEdit = (field) => {
    setIsEditing({ ...isEditing, [field]: true });
  };

  const handleChange = (e, field) => {
    setUserData({ ...userData, [field]: e.target.value });
  };

  const handleSave = (field) => {
    setIsEditing({ ...isEditing, [field]: false });

    // Save updated data to localStorage
    const updatedUser = { ...userData };
    localStorage.setItem("user", JSON.stringify(updatedUser));

    console.log(`${field} updated successfully in localStorage!`);
  };

  return (
    <div className="edit-details-container">
      <h2>Profile Information</h2>

      {/* Username */}
      <div className="field-container">
        <label>Personal Information</label>
        {isEditing.username ? (
          <input type="text" value={userData.username} onChange={(e) => handleChange(e, "username")} />
        ) : (
          <p>{userData.username}</p>
        )}
        {isEditing.username ? (
          <button onClick={() => handleSave("username")}>Save</button>
        ) : (
          <span className="edit-btn" onClick={() => handleEdit("username")}>Edit</span>
        )}
      </div>

      {/* Email */}
      <div className="field-container">
        <label>Email Address</label>
        {isEditing.email ? (
          <input type="email" value={userData.email} onChange={(e) => handleChange(e, "email")} />
        ) : (
          <p>{userData.email}</p>
        )}
        {isEditing.email ? (
          <button onClick={() => handleSave("email")}>Save</button>
        ) : (
          <span className="edit-btn" onClick={() => handleEdit("email")}>Edit</span>
        )}
      </div>

      {/* Mobile Number */}
      <div className="field-container">
        <label>Mobile Number</label>
        {isEditing.mobile ? (
          <input type="tel" value={userData.mobile} onChange={(e) => handleChange(e, "mobile")} />
        ) : (
          <p>{userData.mobile}</p>
        )}
        {isEditing.mobile ? (
          <button onClick={() => handleSave("mobile")}>Save</button>
        ) : (
          <span className="edit-btn" onClick={() => handleEdit("mobile")}>Edit</span>
        )}
      </div>
    </div>
  );
};

export default EditDetails;
