import React, { useState, useEffect } from "react";
import './EditDetails.css';

const EditProfile = () => {
  const [userData, setUserData] = useState({
    username: "",
    email: "",
    mobile: "",
  });
  const [isEditing, setIsEditing] = useState(false);

  // ✅ Fetch User Details When Component Mounts
  useEffect(() => {
    const fetchUserDetails = async () => {
      const token = localStorage.getItem("token"); // Get Token

      const response = await fetch("http://localhost:5000/user-details", {
        method: "GET",
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });

      const data = await response.json();
      if (response.ok) {
        setUserData(data);
      } else {
        console.error("Error fetching user details:", data.error);
      }
    };

    fetchUserDetails();
  }, []);

  // ✅ Handle Input Change
  const handleChange = (e) => {
    setUserData({ ...userData, [e.target.name]: e.target.value });
  };

  // ✅ Handle Save Changes
  const handleSave = async () => {
    const token = localStorage.getItem("token");

    const response = await fetch("http://localhost:5000/update-user", {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer ${token}`,
      },
      body: JSON.stringify(userData),
    });

    const data = await response.json();
    if (response.ok) {
      alert("Profile updated successfully!");
      setIsEditing(false); // Switch back to read-only mode
    } else {
      console.error("Error updating profile:", data.error);
    }
  };

  return (
    <div style={{ maxWidth: "400px", margin: "20px auto", padding: "10px", border: "1px solid #ccc", borderRadius: "5px" }}>
      <h2>Edit Profile</h2>

      {/* Username */}
      <label>Username</label>
      <input
        type="text"
        name="username"
        value={userData.username}
        onChange={handleChange}
        disabled={!isEditing} // Disable when not editing
        style={{ width: "100%", padding: "5px", marginBottom: "10px" }}
      />

      {/* Email */}
      <label>Email</label>
      <input
        type="email"
        name="email"
        value={userData.email}
        onChange={handleChange}
        disabled={!isEditing}
        style={{ width: "100%", padding: "5px", marginBottom: "10px" }}
      />

      {/* Mobile */}
      <label>Mobile Number</label>
      <input
        type="text"
        name="mobile"
        value={userData.mobile}
        onChange={handleChange}
        disabled={!isEditing}
        style={{ width: "100%", padding: "5px", marginBottom: "10px" }}
      />

      {/* Buttons */}
      {!isEditing ? (
        <button onClick={() => setIsEditing(true)} style={{ background: "blue", color: "white", padding: "5px 10px", border: "none", cursor: "pointer" }}>
          Edit
        </button>
      ) : (
        <button onClick={handleSave} style={{ background: "green", color: "white", padding: "5px 10px", border: "none", cursor: "pointer" }}>
          Save
        </button>
      )}
    </div>
  );
};

export default EditProfile;
