import React, { useState } from "react";
import "./DataDisplay.css";
import ReactMarkdown from "react-markdown";

const DataDisplay = ({ data = [], entity = "Data" }) => {
const [isTableView, setIsTableView] = useState(false); // Default: Bullet Points

const toggleView = () => {
setIsTableView((prev) => !prev);
console.log("Toggle View Clicked! New state:", !isTableView); // Debugging log
};

return (
<div className="data-container">
    <h2>{entity} Details</h2>
    <button className="toggle-btn" onClick={toggleView}>
    {isTableView ? "Show Bullet Points" : "Show Table Format"}
    </button>

    {data?.length === 0 ? (
    <p>No data available.</p>
    ) : isTableView ? (
    <table className="data-table">
        <thead>
        <tr>
            {Object.keys(data[0] || {}).map((key, index) => (
            <th key={index}>{key.replace("_", " ").toUpperCase()}</th>
            ))}
        </tr>
        </thead>
        <tbody>
        {data.map((item, index) => (
            <tr key={index}>
            {Object.values(item).map((value, idx) => (
                <td key={idx}>{value}</td>
            ))}
            </tr>
        ))}
        </tbody>
    </table>
    ) : (
    <div className="bullet-list">
        {data.map((item, index) => (
        <div key={index} className="bullet-item">
            <h3>{item.name || `Entry ${index + 1}`}</h3>
            <ul>
            {Object.entries(item).map(([key, value], idx) => (
                <li key={idx}>
                <strong>{key.replace("_", " ")}:</strong>{" "}
                <ReactMarkdown>{String(value)}</ReactMarkdown>
                </li>
            ))}
            </ul>
        </div>
        ))}
    </div>
    )}
</div>
);
};

export default DataDisplay;
