import React, { useState, useEffect } from "react";
import "./EditDetails.css";

const EditDetails = () => {
  const [userData, setUserData] = useState({
    username: "",
    email: "",
    mobile: "",
  });

  const [isEditing, setIsEditing] = useState({
    username: false,
    email: false,
    mobile: false,
  });

  // Fetch user data on component mount
  useEffect(() => {
    // Simulating an API call
    fetchUserData();
  }, []);

  const fetchUserData = async () => {
    try {
      // Replace this with actual API call
      const response = await fetch("https://api.example.com/user"); // Example API
      const data = await response.json();

      setUserData({
        username: data.username || "John Doe",
        email: data.email || "johndoe@example.com",
        mobile: data.mobile || "+919876543210",
      });
    } catch (error) {
      console.error("Error fetching user data:", error);
    }
  };

  const handleEdit = (field) => {
    setIsEditing({ ...isEditing, [field]: true });
  };

  const handleChange = (e, field) => {
    setUserData({ ...userData, [field]: e.target.value });
  };

  const handleSave = async (field) => {
    setIsEditing({ ...isEditing, [field]: false });

    // Simulating API update request (you can replace with real API call)
    try {
      await fetch("https://api.example.com/user/update", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ [field]: userData[field] }),
      });
      console.log(`${field} updated successfully!`);
    } catch (error) {
      console.error("Error updating data:", error);
    }
  };

  return (
    <div className="edit-details-container">
      <h2>Profile Information</h2>

      {/* Username */}
      <div className="field-container">
        <label>Personal Information</label>
        {isEditing.username ? (
          <input type="text" value={userData.username} onChange={(e) => handleChange(e, "username")} />
        ) : (
          <p>{userData.username}</p>
        )}
        {isEditing.username ? (
          <button onClick={() => handleSave("username")}>Save</button>
        ) : (
          <span className="edit-btn" onClick={() => handleEdit("username")}>Edit</span>
        )}
      </div>

      {/* Email */}
      <div className="field-container">
        <label>Email Address</label>
        {isEditing.email ? (
          <input type="email" value={userData.email} onChange={(e) => handleChange(e, "email")} />
        ) : (
          <p>{userData.email}</p>
        )}
        {isEditing.email ? (
          <button onClick={() => handleSave("email")}>Save</button>
        ) : (
          <span className="edit-btn" onClick={() => handleEdit("email")}>Edit</span>
        )}
      </div>

      {/* Mobile Number */}
      <div className="field-container">
        <label>Mobile Number</label>
        {isEditing.mobile ? (
          <input type="tel" value={userData.mobile} onChange={(e) => handleChange(e, "mobile")} />
        ) : (
          <p>{userData.mobile}</p>
        )}
        {isEditing.mobile ? (
          <button onClick={() => handleSave("mobile")}>Save</button>
        ) : (
          <span className="edit-btn" onClick={() => handleEdit("mobile")}>Edit</span>
        )}
      </div>
    </div>
  );
};

export default EditDetails;
