import React, { useState, useEffect } from "react";
import axios from "axios";
import "./EditDetails.css";

const EditDetails = () => {
  const [userData, setUserData] = useState({
    username: "",
    email: "",
    mobile: "",
  });

  const [isEditing, setIsEditing] = useState({
    username: false,
    email: false,
    mobile: false,
  });

  // Fetch user data from the database when the component mounts
  useEffect(() => {
    const fetchUserDetails = async () => {
      try {
        const token = localStorage.getItem("token"); // Get token from localStorage
        if (!token) {
          console.error("No token found, redirect to login.");
          return;
        }

        const response = await axios.get("http://localhost:5000/user-details", {
          headers: { Authorization: `Bearer ${token}` }, // Send token for authentication
        });

        setUserData(response.data); // Update state with fetched data
        localStorage.setItem("user", JSON.stringify(response.data)); // Store in localStorage
      } catch (error) {
        console.error("Error fetching user details:", error.response?.data || error.message);
      }
    };

    fetchUserDetails();
  }, []);

  const handleEdit = (field) => {
    setIsEditing({ ...isEditing, [field]: true });
  };

  const handleChange = (e, field) => {
    setUserData({ ...userData, [field]: e.target.value });
  };

  const handleSave = async (field) => {
    try {
      const token = localStorage.getItem("token");
      if (!token) {
        console.error("No token found, cannot update details.");
        return;
      }

      const updatedUser = { [field]: userData[field] };

      const response = await axios.put("http://localhost:5000/update-user", updatedUser, {
        headers: {
          Authorization: `Bearer ${token}`,
          "Content-Type": "application/json",
        },
      });

      console.log(`${field} updated successfully!`, response.data);

      setIsEditing({ ...isEditing, [field]: false });

      // Update localStorage
      localStorage.setItem("user", JSON.stringify({ ...userData }));

    } catch (error) {
      console.error("Error updating user details:", error.response?.data || error.message);
    }
  };

  return (
    <div className="edit-details-container">
      <h2>Profile Information</h2>

      {/* Username */}
      <div className="field-container">
        <label>Personal Information</label>
        {isEditing.username ? (
          <input type="text" value={userData.username} onChange={(e) => handleChange(e, "username")} />
        ) : (
          <p>{userData.username}</p>
        )}
        {isEditing.username ? (
          <button onClick={() => handleSave("username")}>Save</button>
        ) : (
          <span className="edit-btn" onClick={() => handleEdit("username")}>Edit</span>
        )}
      </div>

      {/* Email */}
      <div className="field-container">
        <label>Email Address</label>
        {isEditing.email ? (
          <input type="email" value={userData.email} onChange={(e) => handleChange(e, "email")} />
        ) : (
          <p>{userData.email}</p>
        )}
        {isEditing.email ? (
          <button onClick={() => handleSave("email")}>Save</button>
        ) : (
          <span className="edit-btn" onClick={() => handleEdit("email")}>Edit</span>
        )}
      </div>

      {/* Mobile Number */}
      <div className="field-container">
        <label>Mobile Number</label>
        {isEditing.mobile ? (
          <input type="tel" value={userData.mobile} onChange={(e) => handleChange(e, "mobile")} />
        ) : (
          <p>{userData.mobile}</p>
        )}
        {isEditing.mobile ? (
          <button onClick={() => handleSave("mobile")}>Save</button>
        ) : (
          <span className="edit-btn" onClick={() => handleEdit("mobile")}>Edit</span>
        )}
      </div>
    </div>
  );
};

export default EditDetails;
