import React, { useState, useEffect } from "react";
import "./DataDisplay.css";
import ReactMarkdown from "react-markdown";

const DataDisplay = ({ data = [], entity = "Data", viewPreference = "paragraph" }) => {
    const [displayMode, setDisplayMode] = useState("paragraph");

    useEffect(() => {
        if (viewPreference === "clear" || viewPreference === "concise") {
            setDisplayMode("bullets");
        } else if (viewPreference === "table" || data.length > 3) {
            setDisplayMode("table");
        } else {
            setDisplayMode("paragraph");
        }
    }, [viewPreference, data.length]);

    return (
        <div className="data-container">
            <h2>{entity} Details</h2>
            {data.length === 0 ? (
                <p>No data available.</p>
            ) : displayMode === "table" ? (
                <table className="data-table">
                    <thead>
                        <tr>
                            {Object.keys(data[0] || {}).map((key, index) => (
                                <th key={index}>{key.replace("_", " ").toUpperCase()}</th>
                            ))}
                        </tr>
                    </thead>
                    <tbody>
                        {data.map((item, index) => (
                            <tr key={index}>
                                {Object.values(item).map((value, idx) => (
                                    <td key={idx}>{value}</td>
                                ))}
                            </tr>
                        ))}
                    </tbody>
                </table>
            ) : displayMode === "bullets" ? (
                <div className="bullet-list">
                    {data.map((item, index) => (
                        <div key={index} className="bullet-item">
                            <h3>{item.name || `Entry ${index + 1}`}</h3>
                            <ul>
                                {Object.entries(item).map(([key, value], idx) => (
                                    <li key={idx}>
                                        <strong>{key.replace("_", " ")}:</strong> <ReactMarkdown>{String(value)}</ReactMarkdown>
                                    </li>
                                ))}
                            </ul>
                        </div>
                    ))}
                </div>
            ) : (
                <div className="paragraph-view">
                    {data.map((item, index) => (
                        <p key={index}>
                            <ReactMarkdown>{Object.entries(item)
                                .map(([key, value]) => `${key.replace("_", " ")}: ${value}`)
                                .join(", ")}</ReactMarkdown>
                        </p>
                    ))}
                </div>
            )}
        </div>
    );
};

export default DataDisplay;
