import React, { useState } from 'react';
import { Edit3, LogOut } from 'lucide-react';
import { motion } from 'framer-motion';
import { useNavigate } from "react-router-dom";
import './profile.css'

const Profile = () => {
const navigate = useNavigate();
const [isOpen, setIsOpen] = useState(false);

const handleLogout = () => {
        localStorage.removeItem('token');
        navigate('/login');
    };

const handleEditDetails = () => {
// Edit details logic here
alert('Editing details...');
};

return (
<div className="relative">

    <button
    onClick={() => setIsOpen(!isOpen)}
    className="w-12 h-12 rounded-full overflow-hidden border-2 border-gray-300 focus:outline-none"
    >
    <img
        src="/profile_image.png" // Replace with actual profile image URL
        alt="Profile"
        className="w-full h-full object-cover"
    />
    </button>

    {isOpen && (
    <motion.div
        initial={{ opacity: 0, y: -10 }}
        animate={{ opacity: 1, y: 0 }}
        exit={{ opacity: 0, y: -10 }}
        className="absolute right-0 mt-2 w-48 bg-white shadow-lg rounded-xl z-50"
    >
        <div className="p-2">
        <button
            onClick={handleEditDetails}
            className="flex items-center gap-2 p-2 hover:bg-gray-100 rounded w-full"
        >
            <Edit3 size={16} /> Edit Details
        </button>
        <button
            onClick={handleLogout}
            className="flex items-center gap-2 p-2 hover:bg-gray-100 rounded w-full text-red-500"
        >
            <LogOut size={16} /> Logout
        </button>
        </div>
    </motion.div>
    )}
</div>
);
};

export default Profile;
