import React, { useState } from 'react';
import { Button } from "@/components/ui/button";
import { Card } from "@/components/ui/card";
import { Menu, MenuItem } from "@/components/ui/menu";
import { motion } from "framer-motion";
import { UserCircle, Settings, LogOut, Edit3 } from "lucide-react";

const Profile = () => {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <div className="relative">
      <button 
        className="w-12 h-12 rounded-full overflow-hidden border-2 border-gray-300 focus:outline-none" 
        onClick={() => setIsOpen(!isOpen)}
      >
        <img src="https://via.placeholder.com/40" alt="Profile" className="w-full h-full object-cover" />
      </button>
      {isOpen && (
        <motion.div 
          initial={{ opacity: 0, y: -10 }}
          animate={{ opacity: 1, y: 0 }}
          exit={{ opacity: 0, y: -10 }}
          className="absolute right-0 mt-2 w-48 bg-white shadow-lg rounded-xl z-50"
        >
          <Card className="p-2">
            <Menu>
              <MenuItem onClick={() => alert('Edit Details')} className="flex items-center gap-2 p-2 hover:bg-gray-100 rounded">
                <Edit3 size={16} /> Edit Details
              </MenuItem>
              <MenuItem onClick={() => alert('Settings')} className="flex items-center gap-2 p-2 hover:bg-gray-100 rounded">
                <Settings size={16} /> Settings
              </MenuItem>
              <MenuItem onClick={() => alert('Logout')} className="flex items-center gap-2 p-2 hover:bg-gray-100 rounded text-red-500">
                <LogOut size={16} /> Logout
              </MenuItem>
            </Menu>
          </Card>
        </motion.div>
      )}
    </div>
  );
};

export default Profile;
